mapper.select:
SELECT acl.al_name AS name, COUNT( acl.al_id ) AS value FROM accidentinfo_t act, accidentlevel_t acl WHERE act.acc_level = acl.al_id
and acc_industryId = #{industryId}
GROUP BY acl.al_id;

SELECT sum( acc_deathSum ) AS acc_deathSum, sum( acc_injuredSum ) AS acc_injuredSum, sum( acc_financial ) AS acc_financial, acc_time FROM accidentinfo_t WHERE YEAR ( acc_time ) = ( SELECT YEAR ( CURRENT_DATE ) AS YEAR )
and acc_industryId = #{industryId}
GROUP BY MONTH ( acc_time ) ORDER BY MONTH ( acc_time )
